<script>
    window.dataLayer = window.dataLayer || [];
    function gtag(){dataLayer.push(arguments);}
    gtag('js', new Date());
    gtag('config', 'XXX');

    let navOpened = false;
    let initHeight = 100;

    function slideToggle() {
        let navMobile = document.getElementById('navbar__nav-mobile');

        if (navOpened) {
            navOpened = false;
            navMobile.style.height = '0';
            navMobile.style.paddingTop = '0';
            document.getElementById('navbar__toggle').classList.remove('navbar__toggle--open');
        } else {
            navOpened = true;
            navMobile.style.height = initHeight + 'vh';
            navMobile.style.paddingTop = '50px';
            document.getElementById('navbar__toggle').classList.add('navbar__toggle--open');
        }
    }

    //fixed navbar
    let scrollpos = window.scrollY
    const main = document.querySelector("main")
    const header = document.querySelector(".navbar")
    const header_height = header.offsetHeight

    const bar_height = document.querySelector(".navbar__bar").offsetHeight

    const add_class_on_scroll = () => {header.classList.add("fixed"); main.classList.add("fixed-mt")}
    const remove_class_on_scroll = () => {header.classList.remove("fixed"); main.classList.remove("fixed-mt")}

    window.addEventListener('scroll', function() {
        scrollpos = window.scrollY;

        if (scrollpos >= bar_height) { add_class_on_scroll() }
        else { remove_class_on_scroll() }
    })

</script>

